mapper.select:
select b.*,b.start_time as startTime, b.end_time as endTime, p.name as placeName, p.id as placeId
from gym_book b
left join gym_place p
on b.place_id = p.id
where b.is_cancel = #{isCancel}

    order by b.${Order}

mapper.update:
update gym_book
set start_time = #{startTime},
end_time = #{endTime},
name = #{name}
where id = #{id}

update gym_book
set is_cancel = 1
where id = #{id}

update gym_place
set state = 0
where id = #{placeId}
